import "../Components/style/Navbar.css"
import React, { useState } from 'react'
import logo from "./Images/logo.png"
import HeroSection from "./HeroSection";
import { Route } from "react-router-dom";
import { Link } from "react-scroll";
function Navbar() {
    //navActive-represents whether the mobile navigation menu is active or not
    const [navActive, setNavActive] = useState(false);

    //when hamburger menu is clicked toggleNav toggles the value of navActive
    const toggleNav = () => {
        setNavActive(!navActive)
    }
    //it sets navActive to false, when mobile menu is closed
    const closeMenu = () => {
        setNavActive(false);
    }
    useEffect(() => {
      const handleResize=()=>{
        if(window.innerWidth<=500){
            closeMenu
        }
      }
      window.addEventListener("resize",handleResize);
      return ()=>{
        window.remo
      }
    })
    
    return (
        <>
            <nav className={`navbar ${navActive ? "active" : ""}`}>
                <div>
                    <img src={logo} alt="Dipika Jain" />
                </div>
                <a href="" className={`nav-hamburger ${navActive ? "active" : ""}`} onClick={toggleNav}>
                    <span className="nav-hamburger-line"></span>
                    <span className="nav-hamburger-line"></span>
                    <span className="nav-hamburger-line"></span>
                </a>
                <nav className={`navbar-items ${navActive ? "active" : ""}`}>
                 <ul>
                    <li><Link to="/HeroSection" >Home</Link></li>
                    <li><Link to="/AboutMe">About Me</Link></li>
                    <li><Link to="/">Experience</Link></li>
                    
                    <li><Link></Link></li>
                    </ul>   
                </nav>
            </nav>
        </>
    )
}

export default Navbar
